mapper.update:
UPDATE user
SET title = #{title},
    contents = #{contents}
WHERE user_id = #{loginedUserId} AND diary_id = #{diaryId}

mapper.select:
SELECT diary_id AS diaryId, title, contents, created_at AS createdAt
FROM diary
WHERE user_id = #{loginedUserId}
ORDER BY diary_id DESC
LIMIT #{startIdx}, #{rowCount}
