mapper.select:
SELECT user_id
FROM user
WHERE nick_name = #{nickName}

SELECT user_id AS userId, upw, nick_name AS nickName
FROM user
WHERE uid = #{uid}

mapper.update:
UPDATE user
SET upw = #{upw}
WHERE user_id = #{loginedUserId}

UPDATE user
SET nick_name = #{nickName}
WHERE user_id = #{loginedUserId}
